#!/usr/bin/env python

def timblast(query,kmerdict,kmer=8):
        #will return None if best hit has a score of zero
        k=kmer
        m=0
        seqdict={}
        while k+m<=len(query):
                try:
                        for element in kmerdict[query[m:m+k]]:
                                try:
                                        seqdict[element]+=1
                                except KeyError:
                                        seqdict[element]=1
                except KeyError:
                        pass
                m+=1

        highvalue=0
        highkey=None
        for key,value in seqdict.iteritems():
                if highvalue<value:
                        highkey=key
                        highvalue=value
        maxscore=len(query)-k
        return highkey,float(highvalue)/float(maxscore)
def make_timblast_ref(inputdict,kmer=8,keylabels=False):
        kmerdict={}
        k=kmer
        m=0
        if not keylabels:#the supplied dictionary has sequences as keys and the name of the sequences as values
                for key in inputdict:
                        while k+m<=len(key):
                                try:
                                        kmerdict[key[m:m+k]].add(inputdict[key])
                                except KeyError:
                                        kmerdict[key[m:m+k]]=set([inputdict[key]])
                                m+=1
                        m=0
                return kmerdict
        else:#keys are sequence names and values are seuqences
                for key in inputdict:
                        while k+m<=len(inputdict[key]):
                                try:
                                        kmerdict[inputdict[key][m:m+k]].add(key)
                                except KeyError:
                                        kmerdict[inputdict[key][m:m+k]]=set([key])
                                m+=1
                        m=0
                return kmerdict
def revcomp(sequence):
        #reverse sequence
        sequence=sequence[::-1]
        #complement sequence
        compdict={'A':'T','T':'A','C':'G','G':'C','N':'N'}
        new=''
        for base in sequence:
                try:
                        new+=compdict[base]
                except KeyError:
                        new+=base
        return new

